import React from "react";
import Icons from "../User/Icons"
import uuidv from "uuid/v4";
import "./Wheel.css";


class Wheel extends React.Component{
    state = {
        icons: ["mrGreen", "mrPink", "mrBlack"],
        wheelColors: []
    };



    componentWillMount(){
        this.randomIcons();
    }

    randomIcons = () => {
        let tempArray = [];
        for(var i = 0; i < 13; i++){
            let random = Math.floor(Math.random() * Math.floor(3));
            tempArray.push(this.state.icons[random]);
            console.log(random);
        }
        this.setState({wheelColors : tempArray});
    }

    render(){

        var spin = keyframes`
            0% { transform: translate(0); }
            100% { transform: translate(500); }
    `   ;

        var styles = {
            border: "16px solid #eee",
            borderTop: "16px solid #3ae",
            borderRadius: "50%",
            width: "1cm",
            height: "1cm",
            animation: `${spin} 2s linear infinite`
        };


        let iconElements = this.state.wheelColors.map((value)=>{
            return <Icons key={uuidv()} className = "icon" userName = {value} />
        });

        return(
            <div className="wheel">
                <div className="iconWrap">
                    {iconElements}
                </div>
            </div>
        );
    };
};

export default (Wheel);
