import React, {Component} from "react"

class Results extends Component{
    state ={
        msgShow: false
    };

    componentWillReceiveProps(props){
        if(props.win !== this.state.msgShow){
            this.setState({
                msgShow: true
            });
            setTimeout(()=>{
                this.setState({
                    msgShow: false
                });
            }, 1500);
        };
    };


    render(){ 
        let msg = this.props.win ? <h1>YOU WIN!</h1> : <h1>YOU LOSE!</h1>;
        return this.state.msgShow ? msg : "";
    };
};

export default (Results);
