import React, {Component} from "react"

class Results extends Component{
    state ={
        msgShow: false
    };

    msgToggle = () => {
        this.setState({
            msgShow: true
        });
        setTimeout(()=>{
            this.setState({
                msgShow: false
            });
        }, 1500);

    };

    render(){ 
        let msg;
        if(this.props.win){
            msg = <h1>YOU WIN!</h1>;
        }else{
            msg = <h1>YOU LOSE!</h1>;
            }
        return this.state.msgShow ? msg : "";
    };
};

export default (Results);
