import React, {Component} from 'react';
import Header from "./components/Header/Header"
import Wheel from "./components/Wheel/Wheel"
import Option from "./components/Option/Option"
import './App.css';

class App extends Component {
  state = {
    loggedIn: true,
    animate: false,
    animateName: "spin spinAnimate"
  };

  spinHandler = ()=>{
    this.setState({animate: true});
    setTimeout(()=>{
      this.setState({animate: false});
    }, 5000);
  }

  render(){
    return( 
      <div className="App">
        <Header />
        <Wheel />
        <div className="botWrapper">
          <Option />
          <button id="spinBtn" className={ this.state.animate ? this.state.animateName : "spin" } onClick={this.spinHandler}>Spin!</button>
        </div>
      </div>
    )};
}

export default App;
